import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import CodeBlock from '../../../';
import ReactMarkdown from 'react-markdown';
import MyTemplet  from '../../../templet';
import CardIMG from '../../../../assets/images/uni.jpg';

const styles = {
  card: {
    width: '100%',
    maxWidth: "400px",
    margin: "auto",
    marginTop: "50px",
    marginBottom: "50px",
  },
  media: {
    height: "300px",
  },
};

function MyInfoCard(props) {
  const { classes } = props;
  let output = (
      <Card className={classes.card}>
        <CardActionArea>
          <CardMedia
            className={classes.media}
            image={CardIMG}
            title="Contemplative Reptile"
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              Sea Urchin
            </Typography>
            <Typography component="p">
               In Japan, sea urchin is known as uni (うに), and its roe can retail for as much as ¥40000 ($360) per kg; it is served raw as sashimi or in sushi, with soy sauce and wasabi.
            </Typography>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button size="small" color="secondary">
            Share
          </Button>
          <Button size="small" color="primary">
            Learn More
          </Button>
        </CardActions>
      </Card>
  );

  let myName = "Info Card";

  let string = `

    import Card from '@material-ui/core/Card';
    import CardActionArea from '@material-ui/core/CardActionArea';
    import CardActions from '@material-ui/core/CardActions';
    import CardContent from '@material-ui/core/CardContent';
    import CardMedia from '@material-ui/core/CardMedia';
    import Button from '@material-ui/core/Button';
    import Typography from '@material-ui/core/Typography';

    return (
        <Card>
          <CardActionArea>
            <CardMedia image={Your IMG}/>
            <CardContent>
              <Typography>
                Your Title
              </Typography>
              <Typography component="p">
                 Your Content
              </Typography>
            </CardContent>
          </CardActionArea>
          <CardActions>
            <Button> option 1 </Button>
            <Button> option 2</Button>
          </CardActions>
       </Card>
    );`;

  let code = (<ReactMarkdown source={string} renderers={{ code: CodeBlock }}/>);

  return (
    <div>
    <MyTemplet content = {output} name = {myName} sampleUsage = {code}/>
    </div>
  );

}

MyInfoCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MyInfoCard);
