import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import MyTemplet  from '../../../templet';
import CodeBlock from '../../../';
import ReactMarkdown from 'react-markdown';

class MyDialog extends React.Component {
  state = {
    open: false,
  };

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  renderNotification()
  {
    return (
      <div className = "dialog-button">
        <Button variant="outlined" onClick={this.handleClickOpen}>
          New Cash Back Offers ! ! Click it ! !
        </Button>
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">{"Anniversary Sale is Coming"}</DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              Get up <b>25% off</b> sale items + Celebrate with an INCREASED <b>15% </b> cash back (was 2%).
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Later
            </Button>
            <Button onClick={this.handleClose} color="primary" autoFocus>
              Shop Now
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }

  render() {
    let myName = "Dialog";
    let output = this.renderNotification();
    let string = `

    import Button from '@material-ui/core/Button';
    import Dialog from '@material-ui/core/Dialog';
    import DialogActions from '@material-ui/core/DialogActions';
    import DialogContent from '@material-ui/core/DialogContent';
    import DialogContentText from '@material-ui/core/DialogContentText';
    import DialogTitle from '@material-ui/core/DialogTitle'

    <div>
      <Button onClick={this.handleClickOpen}>
        Open alert dialog
      </Button>
      <Dialog open={this.state.open} onClose={this.handleClose} >
        <DialogTitle>{"open dialog?"}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Your contents
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleClose}>
            option 1
          </Button>
          <Button onClick={this.handleClose} >
            option 2
          </Button>
        </DialogActions>
      </Dialog>
     </div>`;

    let code = (<ReactMarkdown source={string} renderers={{ code: CodeBlock }}/>);
    return(
      <div>
      <MyTemplet content = {output} name = {myName} sampleUsage = {code}/>
      </div>
    );
  }
}

export default MyDialog;
